package package1;

public class Nested_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=4;
		int b=98;	
		if(a>50)//4>50
		{
			if(a>60)
			{
				System.out.println("a is 77");
			}
			else
			{
				System.out.println("a is lesss no");

			}		
			
		}
		else
		{
			System.out.println("nested if");
		}
	if(a<b)
		{
			if(b>60)
			{
				System.out.println("b is 98");
			}
			else
			{
				System.out.println("b is lesss no");

			}		
			
		}
		else
		{
			System.out.println("nested if");
		}
		
		
		
		
		
	}

}
